import * as React from "react"
import { Frame, useCycle } from "framer"

// Open Preview (CMD + P)
// API Reference: https://www.framer.com/api

function HueSlide() {
    const [twist, cycle] = useCycle(
        { scale: 0.5, rotate: 0 },
        { scale: 1, rotate: 90 }
    )
    const stops = 16
    const hueIncrement = 360 / stops
    let gradientString = ""
    for (let i = 0; i < stops; i++) {
        gradientString += `, hsl(${i * hueIncrement}, 100%, 50%)`
    }

    console.log(`-webkit-linear-gradient(left${gradientString})`)
    return (
        <Frame
            size={"100%"}
            style={{
                // background: `-webkit-linear-gradient(left, hsl(0, 100%, 50%), yellow, green, cyan, blue, violet)`,
                background: `-webkit-linear-gradient(left${gradientString})`,
            }}
        />
    )
}
